mapper.select:
select house.*,street.`DISTRICT_ID` from house
left join street on house.`STREET_ID`=street.`ID`
where house.`ID`=#{id}

select house.*,`type`.`NAME` as tname,street.`NAME` as sname,district.`NAME` as dname from house
left join `type` on type_id=`type`.`ID`
left join street on house.`STREET_ID`=street.`ID`
left join district on street.`DISTRICT_ID`=district.`ID`
where isdel=0

  and TITLE LIKE concat('%',#{title},'%')


  and DISTRICT_ID=#{did}


  and STREET_ID=#{sid}


  and TYPE_ID=#{tid}


  and PRICE > #{startPrice}


  and PRICE < #{endPrice}


  and ispass=#{state}

Order by PUBDATE ASC
